import React, { useState, useEffect } from 'react';
import './App.css';

const initialImages = [
  { src: '/src/assets/images/na1.jpeg', category: 'Nature' },
  { src: '/src/assets/images/city1.jpeg', category: 'City' },
  { src: '/src/assets/images/animals1.jpeg', category: 'Animals' },
  // Add initial images here
];

function App() {
  const [images, setImages] = useState([]);
  const [category, setCategory] = useState('All');
  const [newImage, setNewImage] = useState(null);
  const [newCategory, setNewCategory] = useState('');

  // Load images from local storage if available, else set default images
  useEffect(() => {
    const storedImages = JSON.parse(localStorage.getItem('images'));
    if (storedImages) {
      setImages(storedImages);
    } else {
      setImages(initialImages);
    }
  }, []);

  // Save the updated images to local storage
  useEffect(() => {
    localStorage.setItem('images', JSON.stringify(images));
  }, [images]);

  // Handle the image upload and add to the gallery
  const handleImageUpload = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      const imageUrl = URL.createObjectURL(file);
      setNewImage(imageUrl);
    }
  };

  // Handle adding the new image to the gallery
  const handleAddImage = () => {
    if (newImage && newCategory) {
      setImages([...images, { src: newImage, category: newCategory }]);
      setNewImage(null); // Clear the uploaded image
      setNewCategory(''); // Clear the category input
    } else {
      alert('Please upload an image and specify a category.');
    }
  };

  // Filter images based on the selected category
  const filteredImages = category === 'All'
    ? images
    : images.filter(image => image.category === category);

  return (
    <div className="gallery-container">
      <h1>Image Gallery</h1>

      <div className="category-filter">
        <button onClick={() => setCategory('All')} className={category === 'All' ? 'filter-btn active' : 'filter-btn'}>All</button>
        <button onClick={() => setCategory('Nature')} className={category === 'Nature' ? 'filter-btn active' : 'filter-btn'}>Nature</button>
        <button onClick={() => setCategory('City')} className={category === 'City' ? 'filter-btn active' : 'filter-btn'}>City</button>
        <button onClick={() => setCategory('Animals')} className={category === 'Animals' ? 'filter-btn active' : 'filter-btn'}>Animals</button>
        {/* Add more category buttons if needed */}
      </div>

      {/* Image Upload Section */}
      <div className="upload-section">
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        <input
          type="text"
          placeholder="Enter category"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        />
        <button className="add-btn" onClick={handleAddImage}>Add Image</button>
      </div>

      <div className="image-grid">
        {filteredImages.map((image, index) => (
          <div key={index} className="image-card">
            <img src={image.src} alt={`category-${image.category}`} className="gallery-img" />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
