import React, { useState, useEffect } from 'react';
import ImageCard from './ImageCard';
import CategoryFilter from './CategoryFilter';
import FullScreenImage from './FullScreenImage';


const images = [
    { src: '/img/na1.jpeg', category: 'Nature' },
    { src: '/img/cty2.jpeg', category: 'City' },
    { src: '/img/pic1.jpeg', category: 'Animals' },
    // Add more images here
  ];
  

const categories = ['All', 'Nature', 'City', 'Animals'];

const ImageGallery = () => {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [filteredImages, setFilteredImages] = useState(images);
  const [showFullScreen, setShowFullScreen] = useState(false);
  const [fullScreenImage, setFullScreenImage] = useState(null);

  useEffect(() => {
    if (selectedCategory === 'All') {
      setFilteredImages(images);
    } else {
      setFilteredImages(images.filter(img => img.category === selectedCategory));
    }
  }, [selectedCategory]);

  const handleImageClick = (src) => {
    setFullScreenImage(src);
    setShowFullScreen(true);
  };

  const handleCloseFullScreen = () => {
    setShowFullScreen(false);
  };

  return (
    <div className="gallery-container">
      <CategoryFilter
        categories={categories}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
      />
      <div className="image-grid">
        {filteredImages.map((img, index) => (
          <ImageCard key={index} imgSrc={img.src} onClick={() => handleImageClick(img.src)} />
        ))}
      </div>
      {showFullScreen && <FullScreenImage src={fullScreenImage} onClose={handleCloseFullScreen} />}
    </div>
  );
};

export default ImageGallery;
