import { configureStore } from '@reduxjs/toolkit';
import cartReducer from '../features/cartSlice'; // Ensure this path is correct
import productsReducer from '../features/productsSlice'; // If you have this slice

const store = configureStore({
    reducer: {
        cart: cartReducer,
        products: productsReducer, // Add this only if you have productsSlice
    },
});

export default store;
